function aura_env.updateDisplay(allstates)\
    local now = GetTime()\
    \
    local casters = {}\
    for caster, info in pairs(aura_env.casts) do\
        if info.exp > now then\
            table.insert(casters, caster)\
        end\
    end\
    \
    table.sort(casters, function(a, b)\
            return aura_env.casts[a].exp < aura_env.casts[b].exp\
    end)\
    \
    \
    if #casters >= 1 then\
        local duration = aura_env.watchSpells[aura_env.casts[casters[1]].spellId]\
        local state = allstates[\":\"] or {\
            show = aura_env.myTurn or aura_env.config.monitor,\
            changed = true,\
            name = \"ADD CAST\",\
            progressType = \"timed\",\
            duration = aura_env.casts[casters[1]].dur,\
            expirationTime = aura_env.casts[casters[1]].exp,\
            autoHide = true,\
            playerSpell = aura_env.assignedSpell,\
            icon = select(3, GetSpellInfo(aura_env.assignedSpell or 1)),\
            playSound = aura_env.config.playSound and aura_env.myTurn,\
            myTurn = aura_env.myTurn,\
        }\
        \
        \
        state.changed = true\
        state.myTurn = aura_env.myTurn\
        if not aura_env.assignedUnit then\
            state.name = \"CC ADD\"\
