["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local rand = math.random(6)\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            assignment = aura_env.assignments[rand],\n            autoHide = true\n        }\n    elseif event == \"IGIRA_ASSIGNMENT_EVENT\" and ... then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        for i, affected in pairs(aura_env.affected) do\n            if affected == WeakAuras.myGUID then\n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = 4,\n                    expirationTime = GetTime() + 4,\n                    assignment = aura_env.assignments[i],\n                    autoHide = true\n                }\n                if i == 1 or i == 2 then\n                    WeakAuras.ScanEvents(\"IGIRA_ASSIGNMENT_EVENT_LEFT\", UnitName(\"player\"))\n                elseif i == 3 or i == 4 then\n                    WeakAuras.ScanEvents(\"IGIRA_ASSIGNMENT_EVENT_RIGHT\", UnitName(\"player\"))\n                elseif i == 5 or i == 6 then\n                    WeakAuras.ScanEvents(\"IGIRA_ASSIGNMENT_EVENT_MIDDLE\", UnitName(\"player\"))\n                end\n            end\n        end\n        aura_env.affected = {}\n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" and ... then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 414888 then -- Blistering Spear\n            table.insert(aura_env.affected, destGUID)\n            \n            if #aura_env.affected == 1 then\n                local id = aura_env.id\n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"IGIRA_ASSIGNMENT_EVENT\", id) end)\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" and ... then\n        aura_env.affected = {}\n    end\nend",
