						["type"] = "custom",
						["custom_type"] = "stateupdate",
						["customVariables"] = "{\n    duration = true,\n    expirationTime = true,\n    stacks = true,\n    npcId = {\n        display = \"Npc ID\",\n        type = \"number\",\n    },\n    isPlayer = {\n        display = \"PlayerTargeted\",\n        type = \"bool\",\n    },\n    spellId = {\n        display = \"Spell\",\n        type = \"number\",\n    },\n    spellName = {\n        display = \"Spell Name\",\n        type = \"string\",\n    },\n    interruptible = {\n        display = \"Interruptible\",\n        type = \"bool\",\n    }\n    \n    \n}",
