						["subeventSuffix"] = "_CAST_START",
						["subeventPrefix"] = "SPELL",
						["custom"] = "function(allStates, event, triggerNum, triggerStates)\n    if event == \"OPTIONS\" then\n        local max = math.random(3) \n        for i = 1, max do\n            local duration = math.random(15)\n            allStates[tostring(i)] = {\n                unit = \"player\",\n                stacks = math.random(6),\n                duration = duration,\n                expirationTime = GetTime() + duration,\n                icon = 132212,\n                progressType = \"timed\",\n                spellName = \"Spell Name Goes Here\",\n                autoHide = true,\n                show = true,\n                changed = true,\n            } \n        end\n        return true\n    elseif triggerNum == nil or WeakAuras.IsOptionsOpen() then\n        return\n    end\n    \n    aura_env.triggers = aura_env.triggers or {}\n    aura_env.triggers[triggerNum] = CopyTable(triggerStates)\n    \n    -- make a fresh table of what allStates should be\n    local output = {}\n    for _, triggerStates in pairs(aura_env.triggers) do\n        for _, triggerState in pairs(triggerStates) do\n            --ViragDevTool_AddData(triggerState, \"triggerState:\"..triggerNum..\" key:\"..key)\n            local sourceUnit = triggerState.sourceUnit\n            local destUnit = triggerState.destUnit\n            \n            local spellId = triggerState.spellId\n            if sourceUnit and destUnit\n            and (UnitInParty(destUnit) or UnitInRaid(destUnit) or UnitIsUnit(destUnit, \"player\"))\n            and (aura_env.config.friendly or not UnitIsFriend(\"player\", sourceUnit))\n            and (not aura_env.config.ignoreSelf or not UnitIsUnit(\"player\", sourceUnit))\n            then\n                local sourceGUID = UnitGUID(sourceUnit)\n                local destGUID = UnitGUID(destUnit)\n                if sourceGUID and destGUID then\n                    local spellId2targetKey = spellId..destGUID\n                    local state = output[spellId2targetKey]\n                    \n                    local name, rank, icon, castTime, minRange, maxRange = GetSpellInfo(spellId)\n                    \n                    \n                    if not state then\n                        local npcId = select(6, strsplit('-', sourceGUID or ''))\n                        output[spellId2targetKey] = {\n                            unit = destUnit,\n                            sources = {[sourceGUID] = true},\n                            stacks = 1,\n                            spellId = spellId,\n                            spellName = name,\n                            icon = triggerState.icon,\n                            duration = triggerState.duration,\n                            expirationTime = triggerState.expirationTime,\n                            castType = triggerState.castType,\n                            interruptible = triggerState.interruptible,\n                            npcId = tonumber(npcId) or 0,\n                            isPlayer = UnitIsUnit(\"player\", destUnit),\n                            progressType = \"timed\",\n                            autoHide = true,\n                            show = true,\n                        }\n                    else\n                        if not state.sources[sourceGUID] then -- skip if the cast was handled from an other trigger\n                            state.sources[sourceGUID] = true\n                            state.stacks = state.stacks + 1\n                            if triggerState.expirationTime < state.expirationTime then\n                                state.expirationTime = triggerState.expirationTime\n                                state.duration = triggerState.duration\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    end\n    --ViragDevTool_AddData(output, \"output:\"..triggerNum)\n    --ViragDevTool_AddData(allStates, \"allStates:\"..triggerNum)\n    -- states not in output will be remove\n    for _, v in pairs(allStates) do\n        v.markForRemove = true\n    end\n    -- update \"allStates\" with data from \"output\", keeping track of which states had an update\n    local anyChange = false\n    for spellId2targetKey, outputState in pairs(output) do\n        allStates[spellId2targetKey] = allStates[spellId2targetKey] or {}\n        local state = allStates[spellId2targetKey]\n        state.markForRemove = nil -- don't remove a state when it's key is in ouput\n        local changed = false\n        for field, value in pairs(outputState) do\n            if field ~= \"source\" then -- ignore source field\n                if state[field] ~= value then\n                    state[field] = value\n                    changed = true\n                end\n            end\n        end\n        if changed then\n            state.changed = true\n            anyChange = true\n        end\n    end\n    -- hide states missing in \"ouput\"\n    for _, v in pairs(allStates) do\n        if v.markForRemove then\n            v.show = false\n            v.changed = true\n            anyChange = true\n        end\n    end\n    --ViragDevTool_AddData(allStates, \"allStates after\")\n    --ViragDevTool_AddData(anyChange, \"anyChange\")\n    return anyChange\nend",
