						["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local stages = {\"READY_TO_SOAK\", \"WAITING_TO_SOAK\", \"DONT_SOAK\"}\n        local stage = stages[math.random(#stages)]\n        \n        if stage == \"WAITING_TO_SOAK\" then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = 3,\n                expirationTime = GetTime() + 3,\n                stage = stage,\n                autoHide = true\n            }\n        else\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                stage = stage\n            }\n        end\n    elseif event == \"LIQUID_CUSTOM_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if not aura_env.myAssignment or aura_env.myAssignment < 1 or #aura_env.order == 0 then -- Everyone has soaked their Seeking Inferno, hide states\n            local state = allstates[\"\"]\n            \n            if state then\n                state.show = false\n                state.changed = true\n                \n                return true\n            end\n        elseif aura_env.myAssignment == 1 then -- We are the next person that should soak their Seeking Inferno\n            -- Check if anyone is affected by the Seeking Inferno vulnerability\n            for unit in WA_IterateGroupMembers() do\n                local _, _, _, _, duration, expirationTime = WA_GetUnitDebuff(unit, 426018)\n                \n                if expirationTime then\n                    local remainingTime = expirationTime - GetTime()\n                    \n                    allstates[\"\"] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = duration,\n                        expirationTime = expirationTime,\n                        stage = \"WAITING_TO_SOAK\",\n                        autoHide = true\n                    }\n                    \n                    if not WeakAuras.IsOptionsOpen() then\n                        C_Timer.After(remainingTime + 0.1, function() WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id) end)\n                    end\n                    \n                    return true\n                end\n            end\n            \n            -- If we are here, then it's our turn to soak and nobody is affected by the vulnerability\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                stage = \"READY_TO_SOAK\"\n            }\n            \n            return true\n        else -- We are targeted by Seeking Inferno, but there are others that should soak before us\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                stage = \"DONT_SOAK\"\n            }\n            \n            return true\n        end\n    elseif event == \"LIQUID_SEEKING_INFERNO\" then\n        local GUID = ...\n        \n        table.insert(aura_env.order, GUID)\n        \n        if GUID == WeakAuras.myGUID then\n            SendChatMessage(\"I pressed the macro!\", \"RAID\")\n            \n            aura_env.myAssignment = #aura_env.order\n            \n            WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n        end\n    elseif event == \"LIQUID_PRIVATE_AURA_MACRO\" or event == \"PESCORUS_PRIVATE_AURA_SEND\" then\n        if not aura_env.macroPressed then\n            aura_env.macroPressed = true\n            \n            LiquidWeakAuras:Broadcast(\"LIQUID_SEEKING_INFERNO\", WeakAuras.myGUID)\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, _, spellID = ...\n        \n        if spellID == 426144 then -- Seeking Inferno\n            aura_env.myAssignment = nil\n            aura_env.macroPressed = false\n            aura_env.order = {}\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" then\n            if spellID == 426018 and (aura_env.lastSoak < GetTime() - 0.5) then -- Seeking Inferno\n                aura_env.lastSoak = GetTime()\n                aura_env.clearCount = aura_env.clearCount + 1\n                \n                -- If everyone is cleared, just clean up\n                if aura_env.clearCount % 3 == 0 then\n                    aura_env.myAssignment = nil\n                    aura_env.macroPressed = false\n                    aura_env.order = {}\n                    \n                    WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n                else -- If there are still soaks remaining, remove the first person from the queue\n                    table.remove(aura_env.order, 1)\n                    \n                    if aura_env.myAssignment then\n                        aura_env.myAssignment = aura_env.myAssignment - 1\n                        \n                        WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n                    end\n                end\n            end\n        elseif subEvent == \"UNIT_DIED\" and aura_env.myAssignment then\n            -- If the player that died was assigned before us, decrement our assignment\n            for i = 1, aura_env.myAssignment - 1 do\n                local GUID = aura_env.order[i]\n                \n                if GUID == destGUID then\n                    aura_env.myAssignment = aura_env.myAssignment - 1\n                    table.remove(aura_env.order, i)\n                    \n                    WeakAuras.ScanEvents(\"LIQUID_CUSTOM_EVENT\", aura_env.id)\n                    \n                    break\n                end\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.lastSoak = 0 -- Timestamp of the last Seeking Inferno soak\n        aura_env.myAssignment = nil\n        aura_env.macroPressed = false -- Whether the player pressed their macro\n        aura_env.clearCount = 0 -- Number of clears this cycle\n        aura_env.order = {}\n    end\nend",
