			["adjustedMax"] = "25",
			["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then\n        local name = LiquidAPI and LiquidAPI:GetName(\"player\") or UnitName(\"player\")\n        local classFileName = UnitClassBase(\"player\")\n        \n        return string.format(\"%d. |c%s%s|r\", aura_env.state.index, RAID_CLASS_COLORS[classFileName].colorStr, name)\n    end\n    \n    local unit = aura_env.state and aura_env.state.unit\n    \n    if unit then\n        local name = LiquidAPI and LiquidAPI:GetName(unit) or UnitName(unit)\n        local classFileName = UnitClassBase(unit)\n        \n        return string.format(\"%d. |c%s%s|r\", aura_env.state.index, RAID_CLASS_COLORS[classFileName].colorStr, name)\n    end\nend",
