						["event"] = "Health",
						["unit"] = "player",
						["events"] = "LIQUID_SEEKING_INFERNO, UNIT_SPELLCAST_SUCCEEDED:boss, CLEU:SPELL_AURA_APPLIED:SPELL_AURA_APPLIED_DOSE, ENCOUNTER_START",
						["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, 3 do\n            allstates[i] ={\n                show = true,\n                changed = true,\n                unit = \"player\",\n                index = i\n            }\n        end\n    elseif event == \"LIQUID_SEEKING_INFERNO\" then\n        local GUID = ...\n        local unit = aura_env.GUIDToUnit[GUID]\n        \n        if unit and not aura_env.GUIDsSeen[GUID] then\n            aura_env.macroCount = aura_env.macroCount + 1\n            aura_env.GUIDsSeen[GUID] = true\n            \n            allstates[aura_env.macroCount] = {\n                show = true,\n                changed = true,\n                unit = unit,\n                index = aura_env.macroCount\n            }\n            \n            return true\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, _, spellID = ...\n        \n        if spellID == 426144 then -- Seeking Inferno\n            aura_env.macroCount = 0\n            aura_env.GUIDsSeen = {}\n            \n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, _, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" then\n            if spellID == 426018 and (aura_env.lastSoak < GetTime() - 0.5) then -- Seeking Inferno\n                aura_env.lastSoak = GetTime()\n                aura_env.clearCount = aura_env.clearCount + 1\n                \n                if aura_env.clearCount % 3 == 0 then\n                    for _, state in pairs(allstates) do\n                        state.show = false\n                        state.changed = true\n                    end\n                    \n                    return true\n                else\n                    local state = allstates[aura_env.clearCount % 3]\n                    \n                    if state then\n                        state.show = false\n                        state.changed = true\n                        \n                        return true\n                    end\n                end\n            end\n        elseif subEvent == \"UNIT_DIED\" then\n            \n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.GUIDToUnit = {}\n        aura_env.GUIDsSeen = {} -- GUIDs of players that already pressed the macro, to make sure we only add them once\n        aura_env.lastSoak = 0 -- Timestamp of the last Seeking Inferno soak\n        aura_env.macroCount = 0 -- Number of people that pressed their macro\n        aura_env.clearCount = 0 -- Number of people that soaked their Seeking Inferno\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n    end\nend",
