				["finish"] = {
					["custom"] = "local anchorID = aura_env.region.anchorID\n\nif anchorID then\n    C_UnitAuras.RemovePrivateAuraAnchor(anchorID)\nend",
					["do_custom"] = true,
				},
				["init"] = {
					["custom"] = "if not C_UnitAuras.AddPrivateAuraAppliedSound then return end\nif not aura_env.region.privateAuraSoundIDs then aura_env.region.privateAuraSoundIDs = {} end\n\nlocal function AddPrivateAuraSound(spellID, soundFileName)\n    -- If this spell already has a sound associated with it, remove it\n    if aura_env.region.privateAuraSoundIDs[spellID] then\n        C_UnitAuras.RemovePrivateAuraAppliedSound(aura_env.region.privateAuraSoundIDs[spellID])\n        \n        aura_env.region.privateAuraSoundIDs[spellID] = nil\n    end\n    \n    -- If a sound is selected for this spell, add it\n    if soundFileName ~= 1 then\n        aura_env.region.privateAuraSoundIDs[spellID] = C_UnitAuras.AddPrivateAuraAppliedSound(\n            {\n                unitToken = \"player\",\n                spellID = spellID,\n                soundFileName = soundFileName,\n                outputChannel = \"master\"\n            }\n        )\n    end\nend\n\nfor spellID, soundFileName in pairs(aura_env.config) do\n    spellID = tonumber(spellID)\n    \n    if spellID then\n        if spellID == 421829 then -- Blaze is a special case because it has 6 different spell IDs\n            AddPrivateAuraSound(421826, soundFileName)\n            AddPrivateAuraSound(421827, soundFileName)\n            AddPrivateAuraSound(421825, soundFileName)\n            AddPrivateAuraSound(414187, soundFileName)\n            AddPrivateAuraSound(421828, soundFileName)\n            AddPrivateAuraSound(421829, soundFileName)\n        else\n            AddPrivateAuraSound(spellID, soundFileName)\n        end\n    end\nend",
