			["displayText"] = "%c\n",
			["customText"] = "function()\n    \n    -- thanks Krazyito for code example from his weakaura http://www.mmo-champion.com/threads/1686033-Weak-Auras-Battle-Res-Monitor-Looking-for-feedback!\n    \n    local greyCol = \"|cFFAAAAAA\"\n    local redCol = \"|cFFB40000\"\n    local whiteCol = \"|cFFFFFFFF\"\n    \n    if aura_env.EStart then\n        \n        local charges,_,started,duration = GetSpellCharges(20484)\n        local time = \"\"\n        \n        -- get time until next available ress\n        if started then\n            local timer = duration - (GetTime() - started)\n            time = (\"%d:%02d\"):format(floor(timer/60),mod(timer,60))\n        end\n        -- print(charges)\n        if charges == nil then \n            \n            charges = 0    \n        end\n        local color = whiteCol\n        if charges < 1 then\n            color = redCol\n        else\n            color = whiteCol\n        end\n        \n        return greyCol .. \"Battle ress:|r \".. color .. charges .. \"|r\" .. greyCol .. \"\\nNext in:|r \" .. time  \n        \n    end\n    -- just an example how it will look\n    return greyCol .. \"Battle ress:|r \" .. \"2\" .. greyCol .. \"\\nNext in:|r \" .. \"2:05\"  \n    \nend",
