			["actions"] = {
				["start"] = {
					["custom"] = "if aura_env.region.bar.fg.SetGradientAlpha then\n    aura_env.region.bar.fg:SetGradient(\"HORIZONTAL\", 0.3,0.35,0.97, 0.71,0.34,1)\nelse\n    aura_env.region.bar.fg:SetGradient(\"HORIZONTAL\", CreateColor(0.3,0.35,0.97,1), CreateColor(0.71,0.34,1,1))\nend",
					["do_custom"] = true,
