 if state then\n                        state.show = false\n                        state.changed = true\n                        \n                        return true\n                    end\n                end\n            end\n            \n            if destGUID == WeakAuras.myGUID and spellID == 421858 then -- Ignited Essence\n                local state = allstates[\"\"]\n                \n                if state then\n                    state.value = state.value + 1\n                    state.changed = true\n                    state.show = state.value < 5\n                    \n                    return true\n                end\n            end\n        end\n    end\nend",
