						["spellIds"] = {
						},
						["custom"] = "function()\n    if (aura_env.LastUpdate and aura_env.LastUpdate > GetTime() - aura_env.config[\"UpdateFrequency\"])\n    then\n        return \n    end\n    aura_env.LastUpdate = GetTime()\n    \n    ---- Setup ----------------------------------------------------------------------------------------------------\n    local CurrentTime = GetTime()\n    \n    local OffCooldown = aura_env.OffCooldown\n    local OffCooldownNotCasting = aura_env.OffCooldownNotCasting\n    local GetRemainingAuraDuration = aura_env.GetRemainingAuraDuration\n    local GetRemainingDebuffDuration = aura_env.GetRemainingDebuffDuration\n    local GetRemainingSpellCooldown = aura_env.GetRemainingSpellCooldown\n    local FindTargetDebuffByPlayer = aura_env.FindTargetDebuffByPlayer\n    local IsCasting = aura_env.IsCasting\n    local GetPlayerStacks = aura_env.GetPlayerStacks\n    local GetTargetStacks = aura_env.GetTargetStacks\n    local NGSend = aura_env.NGSend\n    \n    local ids = aura_env.ids\n    aura_env.OutOfRange = false\n    \n    ---- Setup Data -----------------------------------------------------------------------------------------------\n    local CurrentComboPoints = UnitPower(\"player\", Enum.PowerType.ComboPoints)\n    local MaxComboPoints = UnitPowerMax(\"player\", Enum.PowerType.ComboPoints)\n    \n    local OnAnimaPoint = function()\n        if CurrentComboPoints == 2 and C_UnitAuras.GetPlayerAuraBySpellID(323558) ~= nil then\n            return true\n        elseif CurrentComboPoints == 3 and C_UnitAuras.GetPlayerAuraBySpellID(323559) ~= nil then\n            return true\n        elseif CurrentComboPoints == 4 and C_UnitAuras.GetPlayerAuraBySpellID(323560) ~= nil then\n            return true\n        elseif CurrentComboPoints == 5 and C_UnitAuras.GetPlayerAuraBySpellID(324838) ~= nil then\n            return true\n        else \n            return false \n        end\n    end\n    \n    local IsStealthed = WA_GetUnitBuff(\"player\", ids.Subterfuge) or WA_GetUnitBuff(\"player\", ids.ShadowDanceBuff) or WA_GetUnitBuff(\"player\", ids.Stealth)\n    local CPToFinishAt = MaxComboPoints - 1\n    if (IsStealthed) then CPToFinishAt = CPToFinishAt - 1 end\n    \n    local AtMaxComboPoints = CurrentComboPoints >= CPToFinishAt\n    local AtMaxComboPointsInclAnima = OnAnimaPoint() or AtMaxComboPoints\n    \n    local CurrentEnergy = UnitPower(\"player\", Enum.PowerType.Energy)\n    local DanseMacabreStacks = select(3, WA_GetUnitBuff(\"player\", ids.DanseMacabre))\n    if DanseMacabreStacks == nil then DanseMacabreStacks = 0 end\n    \n    local NearbyEnemies = 0\n    for i = 1, 40 do\n        local unit = \"nameplate\"..i\n        if UnitExists(unit) and not UnitIsFriend(\"player\", unit) and WeakAuras.CheckRange(unit, 8, \"<=\") then\n            NearbyEnemies = NearbyEnemies + 1\n        end\n    end\n    \n    -- Only recommend things when something's targeted\n    if not UnitExists(\"target\") then\n        WeakAuras.ScanEvents(\"NG_GLOW_EXTRAS\", {})\n        NGSend(\"Clear\") return end\n    \n    -- RangeChecker (Melee)\n    if IsItemInRange(16114, \"target\") == false then aura_env.OutOfRange = true end\n    \n    ---- No GCDs - Can glow at the same time as a regular ability ------------------------------------------------- \n    local ExtraGlows = {}\n    \n    -- Vanish if not stealthed, and on low Combo Points. If Shadowdust is talented, only when Flag and SoD is on cooldown.\n    if OffCooldown(ids.Vanish) and not IsStealthed and CurrentComboPoints <= 3 then\n        if (IsPlayerSpell(ids.InvigoratingShadowdust) and not OffCooldown(ids.Flagellation) and not OffCooldown(ids.SymbolsOfDeath)) then\n            ExtraGlows.Vanish = true\n        elseif not IsPlayerSpell(ids.InvigoratingShadowdust) then\n            ExtraGlows.Vanish = true\n        end\n    end\n    \n    -- Shadow Dance with low combo points, either during Symbols of Death or at 2 charges.\n    if OffCooldown(ids.ShadowDance) and not WA_GetUnitBuff(\"player\", ids.ShadowDance) and ((CurrentComboPoints <= 3 and WA_GetUnitBuff(\"player\", ids.SymbolsOfDeath)) or GetSpellCharges(ids.ShadowDance) == 2) then\n        ExtraGlows.ShadowDance = true\n    end\n    \n    -- Thistle Tea for energy or if about to cap charges\n    if OffCooldown(ids.ThistleTea) and (CurrentEnergy < 50 or GetSpellCharges(ids.ThistleTea) == 3) then\n        ExtraGlows.ThistleTea = true\n    end\n    \n    -- Symbols of Death on cooldown or with <3s left on buff\n    if OffCooldown(ids.SymbolsOfDeath) and GetRemainingAuraDuration(\"player\", ids.SymbolsOfDeath) < 3 then\n        ExtraGlows.SymbolsOfDeath = true\n    end\n    \n    -- Shadow Blades after Flagellation\n    if OffCooldown(ids.ShadowBlades) and (WA_GetUnitBuff(\"player\", ids.Flagellation) or GetRemainingAuraDuration(\"player\", ids.FlagellationMasteryBuff) > 10) then\n        ExtraGlows.ShadowBlades = true\n    end\n    \n    WeakAuras.ScanEvents(\"NG_GLOW_EXTRAS\", ExtraGlows)\n    \n    \n    ---- Builder Info ------------------------------------------------------------------------------------------\n    local GetBuilder = function()\n        if IsStealthed then\n            -- Backstab during Shadow Blades if you haven't used it to generate a Danse Macabre stack with 3 or fewer targets\n            if OffCooldown(ids.Backstab) and aura_env.BackstabMacabre == false and NearbyEnemies <= 3 then\n                return \"Backstab\"\n            end\n            \n            -- Shadowstrike to consume Premeditation buff or always during Shadow Blades with 3 or less targets\n            if OffCooldown(ids.Shadowstrike) and NearbyEnemies <= 3 and (WA_GetUnitBuff(\"player\", ids.Premeditation) or WA_GetUnitBuff(\"player\", ids.ShadowBlades)) then\n                return \"Shadowstrike\"\n            end\n            \n            -- Shuriken Storm at 3+ targets\n            if OffCooldown(ids.ShurikenStorm) and NearbyEnemies >= 3 then\n                return \"Shuriken Storm\"\n            end\n            \n            -- Shadowstrike\n            if OffCooldown(ids.Shadowstrike) then\n                return \"Shadowstrike\"\n            end\n        else\n            -- Backstab on Single Target\n            if OffCooldown(ids.Backstab) and NearbyEnemies <= 1 then\n                return \"Backstab\"\n            end\n            \n            -- Shuriken Storm\n            if OffCooldown(ids.ShurikenStorm) and NearbyEnemies >= 2 then\n                return \"Shuriken Storm\"\n            end\n        end\n    end\n    WeakAuras.ScanEvents(\"NG_BUILDER_INFO\", GetBuilder())\n    \n    \n    ---- Finisher Info -----------------------------------------------------------------------------------------\n    local GetFinisher = function()\n        if IsStealthed then\n            -- Eviscerate with less than 3 targets\n            if OffCooldown(ids.Eviscerate) and NearbyEnemies <= 2 then\n                return \"Eviscerate\"\n            end\n            \n            -- Black Powder at 3+ targets\n            if OffCooldown(ids.BlackPowder) and NearbyEnemies >= 3 then\n                return \"Black Powder\"\n            end\n        else\n            -- Eviscerate with less than 3 targets\n            if OffCooldown(ids.Eviscerate) and NearbyEnemies <= 2 then\n                return \"Eviscerate\"\n            end\n            \n            -- Black Powder at 3+ targets\n            if OffCooldown(ids.BlackPowder) and NearbyEnemies >= 3 then\n                return \"Black Powder\"\n            end\n        end\n    end\n    WeakAuras.ScanEvents(\"NG_FINISHER_INFO\", GetFinisher())\n    \n    ---- Normal GCDs -------------------------------------------------------------------------------------------\n    \n    -- Flagellation with enough CP for finisher. Only if Shadow Blades has <14s cooldown left.\n    if OffCooldown(ids.Flagellation) and AtMaxComboPoints and GetRemainingSpellCooldown(ids.ShadowBlades) < 14 then\n        NGSend(\"Flagellation\") return end\n    \n    -- Echoing Reprimand\n    if OffCooldown(ids.EchoingReprimand) then\n        NGSend(\"Echoing Reprimand\") return end\n    \n    -- Sepsis\n    if OffCooldown(ids.Sepsis) then\n        NGSend(\"Sepsis\") return end\n    \n    -- Shuriken Tornado with Symbols of Death in 2+ targets\n    if OffCooldown(ids.ShurikenTornado) and NearbyEnemies >= 2 and WA_GetUnitBuff(\"player\", ids.SymbolsOfDeath) then\n        NGSend(\"Shuriken Tornado\") return end\n    \n    -- Shuriken Tornado in ST without Shadow Dance, Symbols of Death or Flagellation active\n    if OffCooldown(ids.ShurikenTornado) and NearbyEnemies == 1 and not (WA_GetUnitBuff(\"player\", ids.SymbolsOfDeath) or IsStealthed or WA_GetUnitAura(\"target\", ids.Flagellation, \"PLAYER|HARMFUL\"))  then\n        NGSend(\"Shuriken Tornado\") return end\n    \n    if (IsStealthed) then\n        -- Secret Technique if on 3 or more stacks of Danse Macabre\n        if OffCooldown(ids.SecretTechnique) and AtMaxComboPoints and DanseMacabreStacks >= 3 then\n            NGSend(\"Secret Technique\") return end        \n    else\n        -- Slice And Dice if not up or with less than 12 seconds remaining\n        if AtMaxComboPoints and GetRemainingAuraDuration(\"player\", ids.SliceAndDice) < 12 and OffCooldown(ids.SliceAndDice) then\n            NGSend(\"Slice and Dice\") return end\n        \n        -- Rupture if not up or with less than 9 seconds remaining\n        if AtMaxComboPoints and GetRemainingDebuffDuration(\"target\", ids.Rupture) < 9 and OffCooldown(ids.Rupture) then\n            NGSend(\"Rupture\") return end\n    end\n    \n    NGSend(\"Clear\")\nend",
