						["dynamicDuration"] = false,
						["custom"] = "function(event,arg1,_,_,unit,...) --Calculates roll grade and displays it. Also displays buffs extended by kir when kir is used.\n    if event == \"RTB_DISPLAY_REFRESH\" and arg1 ~= nil then return aura_env.config.ResultMode < 4 end\n    if event == \"RTB_DISPLAY_REFRESH\" and arg1 == nil then return end\n    if unit ~= UnitGUID(\"player\") or (select(8,...) ~= 315508 and select(8,...) ~= 381989) then return end\n    local a = aura_env\n    local b = aura_env.config\n    a.Explanation = \" \"\n    local rankSolo = \"\";\n    --local Matches = 0;\n    \n    local HasT314P = select(1,WeakAuras.GetNumSetItemsEquipped(1566)) >= 4\n    \n    if b.RTB == false or a.FailState == true or #a.BuffVal == 0  then return false end\n    \n    -- if unit == UnitGUID(\"player\") then\n    if select(8,...) == 315508 then --Roll the Bones is used\n        if a.ItsTimer ~= nil then \n            a.ItsTimer:Cancel();\n        end\n        local Apply = GetTime()\n        local Score = 0;\n        --a.BuffApplied = {}\n        -- if #a.BuffApplied > 0 then\n        a.BuffApplied = table.wipe(a.BuffApplied) \n        -- end\n        a.Output = \"\"\n        local Matches = 0;\n        \n        local AttemptCount = 1\n        \n        --Retries to get buffs up to 10 times with more lenient time frames (If you run too lenient time frames, you run the risk of allowing CTO procs in)\n        while(Score == 0 and AttemptCount <= 10) do\n            Score, Matches, rankSolo = aura_env.GetRolls(Score, Matches, 0.05 + AttemptCount / 100 * 1.7, 0.1 + AttemptCount / 100 * 1.5, Apply) \n            AttemptCount = AttemptCount + 1\n        end\n        \n        if Matches > 0 and ((tContains(a.AllowedRollCount, Matches) == false and HasT314P == false) \n            or (tContains(a.AllowedRollCount4p, Matches) == false and HasT314P == true)) then\n            print(\"|cFF00FF00\"..a.id..\"|r - |cFFFF0000Warning!|rRTB returned unexpected amount of buffs!\") --Warning user soemthing is not right\n            local lowestIndex = 0;\n            local lowestValue = false;\n            for k, v in ipairs(a.BuffApplied) do\n                local ApplicationTime = select(6, WA_GetUnitBuff(\"player\", v)) - select(5, WA_GetUnitBuff(\"player\", v))\n                if not lowestValue or ApplicationTime < lowestValue then\n                    lowestIndex = k;\n                    lowestValue = ApplicationTime;\n                end\n            end\n            tremove(a.BuffApplied, lowestIndex)\n            \n            Score = 0;\n            a.Explanation = \"\";\n            a.Output = \"\";\n            Matches = 0;\n            \n            for i = 1, 6 do\n                \n                local icon = select(3,GetSpellInfo(a.Rolls[i]))\n                \n                if tContains(a.BuffApplied, a.Rolls[i])then\n                    if a.AoEScenario == true and a.Rolls[i] == 193358 and a.AoEBonus > 0 then\n                        Score = Score + a.AoEBonus\n                        rankSolo = b.SoloPrint\n                    end\n                    \n                    Matches = Matches + 1      \n                    \n                    Score = Score + a.BuffVal[i]\n                    a.Explanation = a.Explanation..a.buffExp[i]\n                    a.Output = a.Output..\"|T\"..icon..a.IconSize;\n                end\n            end\n        end\n        \n        --if no rolls were gotten, retry with small delay\n        \n        a.KirBuffs() --prevents aura from prompting reroll in cases where kir buffs would be lost\n        \n        --if b.Sleight == true and Matches == 1 and Score >= a.Reqs[5] then Score = a.Reqs[6] rankSolo = \"~\" end \n        \n        if Score > 0 and a.Output ~= \"\" then \n            local Decision = \"\"\n            for z = 1, #a.Reqs do\n                if Score >= a.Reqs[z] then \n                    if b.ResultMode == 1 or b.ResultMode == 3 then\n                        Decision = \"|\"..a.Ranks[z]