						["custom_type"] = "event",
						["unit"] = "player",
						["custom"] = "function(e,f)\n    if e == \"WA_MUROPAKETI_RTB_GET_BUILD_INFO\" and f == nil then return end\n    if ((e == \"CVAR_UPDATE\") and f ~= \"nameplateShowEnemies\") or ((e == \"WA_DELAYED_EQUIPMENT_UPDATE\") and f == nil) then return end\n    local a = aura_env\n    local c = a.config\n    a.KirReq = c.KirReg\n    \n    if e == \"PLAYER_EQUIPMENT_CHANGED\" then C_Timer.After(0.5, function() WeakAuras.ScanEvents(\"WA_DELAYED_EQUIPMENT_UPDATE\", true) end) return end --Delays aura firing when changing equipments\n    \n    if (e == \"ADDON_LOADED\" and f ~= nil) or e == \"LOADING_SCREEN_DISABLED\" then C_Timer.After(3.75, function() WeakAuras.ScanEvents(\"WA_ADDON_LOADED_DELAYED\", true) end) return end --used to initially load build during login\n    \n    --if e == \"PLAYER_ENTERING_WORLD\" and f == true then return end --Not doing anything on this event during initial login\n    \n    if ((e == \"CVAR_UPDATE\") and f == \"nameplateShowEnemies\") or e ~= \"CVAR_UPDATE\" then\n        \n        if GetCVar(\"nameplateShowEnemies\") == \"0\" and c.BladeFlurry == true and c.Warnings == true and WeakAuras.IsOptionsOpen() == false and a.NamePlateErr == false then\n            a.NamePlateErr = true\n            print(\"\\nWA - |cFF00FF00\"..a.id..\"|r - |cFFFFFF00Warning!|r\\nBlade flurry reminder requires enemy nameplates to be enabled.\")\n        end \n        \n        if e == \"CVAR_UPDATE\" then\n            return \n        end\n    end \n    \n    if a.LastTriggered + 0.2 > GetTime() then return end --Don't want this part of the aura to run too often. Just in case where multiple events are fired at once. This limits it to firing at max once every 0.2 sec.\n    \n    a.LastTriggered = GetTime()\n    \n    if e == \"TRAIT_CONFIG_UPDATED\" then \n        a.RTBModified = {false,false,false,false,false,false}\n        a.RtbVValModErr = {false,false,false,false,false,false}\n    end\n    \n    if not InCombatLockdown() or #a.BuffVal == 0 then\n        --[[