				},
				["init"] = {
					["custom"] = "local hasted_tick_times = {\n    -- Mage\n    [205021] = 1.0, -- Ray of Frost\n    [12051]  = 1.0, -- Evocation\n    [314791] = 1.0, -- Shifting Power (covenant)\n    [382440] = 1.0, -- Shifting Power (talent)\n    \n    -- Warlock\n    [198590] = 1.0, -- Drain Soul\n    [234153] = 1.0, -- Drain Life\n}\n\nlocal unhasted_tick_times = {\n    -- Misc\n    [339232] = 3.0, -- Soul Infusion (Castle Nathria - Sun King's Salvation)\n}\n\nlocal initial_ticks = {\n    [5143] = 0.1, -- Arcane Missiles\n}\n\nlocal blizzard_castbar = CastingBarFrame or PlayerCastingBarFrame\nif blizzard_castbar and aura_env.config.hide_blizzard_castbar then\n    blizzard_castbar:UnregisterAllEvents() -- Hide the Blizard Castbar\nend\n\nlocal tick_width = aura_env.config.tick_width / 100\n\nfunction aura_env.get_aura_stacks(spell_id)\n    if GetPlayerAuraBySpellID then\n        return select(3, GetPlayerAuraBySpellID(spell_id))\n    else\n        local info = C_UnitAuras.GetPlayerAuraBySpellID(spell_id)\n        if info then\n            return info.applications\n        end\n    end\nend\n\nlocal function clearcasting_active()\n    return aura_env.clearcasting and aura_env.clearcasting > 0 or aura_env.clearcasting_time and math.abs( GetTime() - aura_env.clearcasting_time ) < aura_env.config.cc_threshold\nend\n\nlocal custom_tick_times = {\n    -- Mage\n    [5143] = function() -- Arcane Missiles\n        return (clearcasting_active() and 0.2875 or 0.625) / (1 + UnitSpellHaste(\"player\") / 100)\n    end,\n    \n    -- Evoker\n    [356995] = function() -- Disintegrate\n        return (IsPlayerSpell(369913) and 0.8 or 1.0) / (1 + UnitSpellHaste(\"player\") / 100)\n    end,\n}\n\nlocal function get_tick_time(spellId)\n    if custom_tick_times[spellId] then\n        return custom_tick_times[spellId]()\n    end\n    if hasted_tick_times[spellId] then\n        return hasted_tick_times[spellId] / (1 + UnitSpellHaste(\"player\") / 100)\n    end\n    if unhasted_tick_times[spellId] then\n        return unhasted_tick_times[spellId]\n    end\nend\n\nlocal function arcane_missiles_duration()\n    local duration = 2.5\n    if clearcasting_active() then\n        duration = 2.0\n    end\n    duration = duration / (1 + UnitSpellHaste(\"player\") / 100)\n    return duration\nend\n\nfunction aura_env.update_cast(event)\n    local name, _, texture, startTimeMS, endTimeMS, _, _, _, spellId = UnitCastingInfo(\"player\")\n    if not name then\n        aura_env.casting = nil\n        return nil\n    end\n    aura_env.channeling = nil\n    aura_env.chaining = nil\n    aura_env.casting = spellId\n    local duration = (endTimeMS - startTimeMS) / 1000\n    local end_time = endTimeMS / 1000\n    return aura_env.update_bar(name, texture, duration, end_time, false, nil)\nend\n\nfunction aura_env.update_channel(event)\n    local name, _, texture, startTimeMS, endTimeMS, _, _, spellId, isEmpower, empowerStages = UnitChannelInfo(\"player\")\n    if isEmpower == 1 then\n        endTimeMS = endTimeMS + GetUnitEmpowerHoldAtMaxTime(\"player\")\n    end\n    if not name then\n        aura_env.channeling = nil\n        aura_env.chaining = nil\n        aura_env.prev_start = nil\n        aura_env.prev_ticks = nil\n        return nil\n    end\n    if event == \"UNIT_SPELLCAST_CHANNEL_START\" then\n        aura_env.chaining = aura_env.channeling == spellId\n        aura_env.next_tick = nil\n        if aura_env.prev_ticks and aura_env.prev_start then\n            for _, t in ipairs(aura_env.prev_ticks) do\n                if t + aura_env.prev_start > GetTime() then\n                    aura_env.next_tick = t + aura_env.prev_start\n                    break\n                end\n            end\n        end\n    end\n    aura_env.casting = nil\n    aura_env.channeling = spellId\n    local start_time = startTimeMS / 1000\n    local end_time = endTimeMS / 1000\n    local tick_time = get_tick_time(spellId)\n    local first_tick = 0\n    if aura_env.chaining and aura_env.next_tick then\n        first_tick = aura_env.next_tick - start_time\n    end\n    local duration = end_time - start_time\n    if first_tick > 0 and spellId == 5143 then -- Arcane Missiles has an unusual bug that can give it extra duration beyond what UnitChannelInfo returns\n        if aura_env.config.custom_duration then\n            duration = arcane_missiles_duration()\n        end\n        if event == \"UNIT_SPELLCAST_CHANNEL_START\" or aura_env.config.custom_duration then\n            duration = first_tick + ( math.ceil( first_tick / tick_time ) - 1 + math.floor( duration / tick_time + 0.5 ) ) * tick_time\n        else\n            duration = duration + ( math.ceil( first_tick / tick_time ) - 1 ) * tick_time\n        end\n    end\n    local ticks = nil\n    if isEmpower == 1 then\n        ticks = {}\n        local t = 0\n        for i = 1, empowerStages do\n            t = t + GetUnitEmpowerStageDuration(\"player\", i - 1) / 1000\n            ticks[i] = t\n        end\n    elseif tick_time then\n        local t = first_tick\n        ticks = {}\n        local tick_zero = initial_ticks[spellId]\n        if tick_zero then\n            ticks[1] = tick_zero\n        end\n        if t > 0 then\n            ticks[#ticks+1] = t\n        end\n        while t < duration do\n            t = t + tick_time\n            if t > duration then\n                t = duration\n            end\n            ticks[#ticks + 1] = t\n        end\n    end\n    aura_env.prev_ticks = ticks\n    aura_env.prev_start = start_time\n    return aura_env.update_bar(name, texture, duration, start_time + duration, isEmpower == 0, ticks)\nend\n\nfunction aura_env.update_bar(name, icon, duration, end_time, channel, ticks)\n    local tick_positions = {}\n    if ticks then\n        for _, t in ipairs(ticks) do\n            tick_positions[#tick_positions + 1] = {\n                min = duration - (t - duration * tick_width),\n                max = duration - (t + duration * tick_width),\n            }\n        end\n    end\n    local state = {\n        show = true,\n        changed = true,\n        name = name,\n        icon = icon,\n        progressType = \"timed\",\n        duration = duration,\n        expirationTime = end_time,\n        autoHide = true,\n        inverse = channel,\n        orientation = channel and \"HORIZONTAL\" or \"HORIZONTAL_INVERSE\",\n        additionalProgress = tick_positions,\n    }\n    aura_env.update_tick_colors()\n    return state\nend\n\nfunction aura_env.update_tick_colors()\n    local region = WeakAuras.GetRegion(aura_env.id, \"castbar\")\n    if not region then\n        return\n    end\n    local ticks = region.bar.additionalBars\n    if ticks then\n        for i,_ in ipairs(ticks) do\n            region.bar:SetAdditionalBarColor(i, aura_env.config.tick_color)\n        end\n    end\nend",
