			},
			["displayText_format_p_time_dynamic_threshold"] = 60,
			["customText"] = "function()\n    if aura_env.saved.enabled == false then\n        return WrapTextInColorCode(\"This weakaura is currently disabled, run /wqr enable to activate it\", \"ffff0000\") \n    end\n    if aura_env.displayListSorted then\n        local text = \"\"\n        local total = {}\n        \n        text = text .. aura_env.customGlobals.displayTitle\n        \n        if #aura_env.displayListSorted > 0 then\n            for _, zoneMaps in ipairs(aura_env.displayListSorted) do\n                for zoneId, wqList in pairs(zoneMaps) do\n                    local mapInfo = C_Map.GetMapInfo(zoneId)\n                    local mapName = mapInfo.name\n                    \n                    text = text .. (aura_env.MapIsCurrent(zoneId) and WrapTextInColorCode(mapName, \"0000FF00\") or (aura_env.MapSharesParent(zoneId) and WrapTextInColorCode(mapName, \"00FFA500\") or mapName)) .. \":\\n\"\n                    for questId, rewards in pairs(wqList) do\n                        text = text .. GetQuestLink(questId) .. (aura_env.QuestIsPetBattle(questId) and \"|T613074:15|t\" or \"\") .. \" - \"\n                        for itemID, rewardInfo in pairs(rewards) do\n                            if rewardInfo.numItems > 0 then\n                                text = text .. \"|T\" .. rewardInfo.texture .. \":15|t\" ..  \" x\" .. rewardInfo.numItems .. \", \"\n                            else\n                                text = text .. rewardInfo.itemName .. \", \"\n                            end\n                            total[itemID] = total[itemID] or {}\n                            local currentCount = total[itemID].count or 0\n                            total[itemID] = {\n                                itemName = rewardInfo.itemName,\n                                count = currentCount + rewardInfo.numItems\n                            }\n                        end\n                        text = text:sub(1, -3)\n                        text = text .. \"\\n\"\n                    end\n                end \n                text = text .. \"\\n\"\n            end\n            \n            text = text .. \"\\nTotal:\\n\"\n            for _, rewardInfo in pairs(total) do\n                text = text .. rewardInfo.itemName .. \": x\" .. rewardInfo.count .. \"\\n\"\n            end\n        else\n            text = text ..  \"No tracked world quests active\\n\"\n        end\n        \n        text = text .. aura_env.customGlobals.footerCap\n        return text\n    end\nend",
			["yOffset"] = -42.50022775155529,
			["anchorPoint"] = "TOPLEFT",
			["displayText_format_p_time_format"] = 0,
			["customTextUpdate"] = "event",
			["automaticWidth"] = "Auto",
			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "aura_env.saved = aura_env.saved or {}\naura_env.saved.questCache = aura_env.saved.questCache or {}\nif aura_env.saved.enabled == nil then\n    aura_env.saved.enabled = true\nend\naura_env.customGlobals = {}\naura_env.customGlobals.title = WrapTextInColorCode(aura_env.id, \"ffE6CC80\")\naura_env.customGlobals.displayTitle = WrapTextInColorCode(aura_env.id .. \"\\n----------------------------\\n\", \"ffE6CC80\")\naura_env.customGlobals.footerCap = WrapTextInColorCode(\"----------------------------\\n\", \"ffE6CC80\")\n\n--Registering slash commands\nLibStub(\"AceConsole-3.0\"):RegisterChatCommand(\"WQR\", function(msg)\n        local command, arg = msg:match(\"^(%S*)%s*(.-)$\")\n        if command == \"enable\" then\n            WeakAuras.ScanEvents(\"WQR_ENABLE\")\n        elseif command == \"disable\" then\n            WeakAuras.ScanEvents(\"WQR_DISABLE\")\n        elseif command == \"removewaypoint\" then\n            WeakAuras.ScanEvents(\"WQR_REMOVE_WAYPOINT\", arg)\n        elseif command == \"removeallwaypoints\" then\n            WeakAuras.ScanEvents(\"WQR_REMOVE_ALL_WAYPOINTS\")\n        elseif command == \"resetcache\" then\n            WeakAuras.ScanEvents(\"WQR_RESET_CACHE\")\n        else\n            print(\"WQR: Unknown command\")\n        end\nend)\n\nlocal function PopulateZoneList()\n    aura_env.uiMapIDs = {}\n    local uiMapIDs = aura_env.uiMapIDs\n    if aura_env.config.expansions[1] then -- Legion enabled\n        uiMapIDs[1] = { --- Broken Isles\n            627, -- Dalaran\n            630, -- Azsuna\n            634, -- Stormheim\n            641, -- Val'sharah\n            646, -- The Broken Shore\n            650, -- Highmountain\n            680, -- Suramar,\n            790, -- Eye of Azshara\n            830, -- Krokuun\n            882, -- Eredath\n            885, -- Antoran Wastes\n        }\n    end\n    if aura_env.config.expansions[2] then -- Battle for Azeroth enabled\n        uiMapIDs[2] = { --- Kalimdor ---\n            1527, -- Uldum\n        }\n        uiMapIDs[3] = { --- Pandaria ---\n            1530, -- Vale of Eternal Blossoms\n        }\n        uiMapIDs[4] = { --- Zandalar ---\n            862, -- Zuldazar\n            863, -- Nazmir\n            864, -- Vol'dun\n            1504, -- Nazjatar\n        }\n        uiMapIDs[5] = { --- Kul Tiras ---\n            895, -- Tiragarde Sound\n            896, -- Drustvar\n            942, -- Stormsong Valley\n            1462, -- Mechagon Island\n        }\n    end\n    if aura_env.config.expansions[3] then -- Shadowlands enabled\n        uiMapIDs[6] = { --- Shadowlands ---\n            1525, -- Revendreth\n            1536, -- Maldraxxus\n            1565, -- Ardenweald\n            1569, -- Bastion\n        }\n    end\n    if aura_env.config.expansions[4] then -- Dragonflight enabled\n        uiMapIDs[7] = { --- Dragon Isles ---\n            2022, -- The Waking Shores\n            2023, -- Ohn'Aran Plains\n            2024, -- The Azure Span\n            2025, -- Thaldraszus\n            2151, -- The Forbidden Reach\n            2133, -- Zaralek Cavern\n        }\n    end\nend\n\nPopulateZoneList()\n\nlocal function Count(table)\n    local size = 0\n    for _ in pairs(table) do size = size + 1 end \n    return size\nend\n\n--Function for checking if an table contains a value\nlocal function TableContains(table, value)\n    for _, v in pairs(table) do\n        if v.itemId == value and v.active then\n            return true\n        end\n    end\n    return false\nend\n\nlocal function ResetActiveQuests()\n    for _, questInfo in pairs(aura_env.saved.questCache) do\n        if questInfo then\n            questInfo.active = false\n        end\n    end \nend\n\nlocal function ProcessRewardInfo(questId, continentID, displayList, itemId, itemName, numItems, texture)\n    local map = C_TaskQuest.GetQuestZoneID(questId)\n    \n    displayList[continentID] = displayList[continentID] or {}\n    displayList[continentID][map] = displayList[continentID][map] or {}\n    \n    if not(displayList[continentID][map][questId]) then\n        displayList[continentID][map][questId] = {}\n    end\n    \n    if not(displayList[continentID][map][questId][itemId]) then\n        displayList[continentID][map][questId][itemId] = {\n            itemName = itemName,\n            numItems = numItems,\n            texture = texture\n        }\n    end\n    \n    if not(aura_env.saved.questCache[questId]) then\n        aura_env.saved.questCache[questId] = {}\n    end\n    \n    aura_env.saved.questCache[questId].active = true\n    \n    if not(aura_env.saved.questCache[questId].waypoint) then\n        aura_env.saved.questCache[questId].waypoint = false\n    end\n    \n    return displayList\nend\n\naura_env.GetWorldQuests = function()\n    local displayList = {}\n    \n    local uiMapIDs = aura_env.uiMapIDs\n    \n    ResetActiveQuests()\n    for continentID, zoneMapIDs in pairs(uiMapIDs) do\n        for _, mapID in pairs(zoneMapIDs) do\n            local tasks = C_TaskQuest.GetQuestsForPlayerByMapID(mapID) \n            \n            if tasks then\n                for i = 1, #tasks do\n                    local task = tasks[i]\n                    local questId = task.questId\n                    local _, factionId = C_TaskQuest.GetQuestInfoByQuestID(questId)\n                    \n                    if TableContains(aura_env.config.items, tostring(factionId)) then\n                        if C_QuestLog.DoesQuestAwardReputationWithFaction(questId, factionId) then\n                            local factionName = GetFactionInfoByID(factionId)\n                            displayList = ProcessRewardInfo(questId, continentID, displayList, factionId, factionName .. \" reputation\", 0, nil)\n                        end\n                    end\n                    \n                    if GetQuestLink(questId) then                        \n                        local numRewards = GetNumQuestLogRewards(questId)\n                        local numCurrencyRewards = GetNumQuestLogRewardCurrencies(questId)\n                        local goldReward = GetQuestLogRewardMoney(questId)\n                        \n                        if numRewards > 0 then\n                            for rewardIndex = 1, numRewards do\n                                local itemName, texture, numItems, _, _, itemId = GetQuestLogRewardInfo(rewardIndex, questId)\n                                \n                                if itemName then\n                                    itemId = tostring(itemId)\n                                    if itemId and TableContains(aura_env.config.items, itemId) then\n                                        displayList = ProcessRewardInfo(questId, continentID, displayList, itemId, itemName, numItems, texture)\n                                    end\n                                end\n                            end\n                        end\n                        \n                        if numCurrencyRewards > 0 then\n                            for rewardIndex = 1, numCurrencyRewards do\n                                local curName, texture, numItems, currencyId = GetQuestLogRewardCurrencyInfo(rewardIndex, questId)\n                                \n                                if curName then \n                                    currencyId = tostring(currencyId)\n                                    if currencyId and TableContains(aura_env.config.items, currencyId) then\n                                        displayList = ProcessRewardInfo(questId, continentID, displayList, currencyId, curName, numItems, texture)\n                                    end\n                                end\n                            end\n                        end\n                        \n                        if goldReward > 0 and TableContains(aura_env.config.items, \"gold\") then\n                            displayList = ProcessRewardInfo(questId, continentID, displayList, 0, \"Gold\", goldReward / 10000, \"Interface\\\\Icons\\\\INV_Misc_Coin_01:16:16:0:0:64:64:4:60:4:60\")\n                        end\n                    end\n                end\n            end\n        end\n    end\n    \n    aura_env.displayListSorted = {}\n    if Count(displayList) > 0 then\n        local zones = {}\n        local i = 1\n        \n        for zone, _ in pairs(displayList) do\n            zones[i] = zone\n            i = i + 1\n        end\n        \n        table.sort(zones)\n        \n        for key, zone in pairs(zones) do\n            aura_env.displayListSorted[key] = displayList[zone]\n        end\n    end\nend\n\naura_env.MapIsCurrent = function(mapId)\n    local currentMapId = C_Map.GetBestMapForUnit(\"player\")\n    \n    return mapId == currentMapId\nend\n\nlocal function RecursiveCheck(mapId1, mapId2)\n    local map2Info = C_Map.GetMapInfo(mapId2)\n    \n    if mapId1 == mapId2 then\n        return true\n    elseif map2Info.mapType > 0 then\n        return RecursiveCheck(mapId1, map2Info.parentMapID) \n    end\n    \n    return false\nend\n\naura_env.MapSharesParent = function(mapId)\n    local mapInfo = C_Map.GetMapInfo(mapId)\n    local currentMapId = C_Map.GetBestMapForUnit(\"player\")\n    \n    if currentMapId then\n        local currentMapInfo = C_Map.GetMapInfo(currentMapId)\n        return RecursiveCheck(mapInfo.parentMapID, currentMapInfo.mapID)\n    else\n        return false\n    end\nend\n\naura_env.QuestIsPetBattle = function(questID)\n    local questInfo = C_QuestLog.GetQuestTagInfo(questID)\n    if questInfo == nil then return false end\n    if questInfo.worldQuestType == 4 then\n        return true\n    end\n    \n    return false\nend\n\naura_env.GetUnitNameAndID = function(unit)\n    if UnitExists(unit) then\n        local npcID = tonumber((UnitGUID(unit) or \"\"):match(\".-%-%d+%-%d+%-%d+%-%d+%-(%d+)\"))\n        local name = UnitName(unit)\n        if npcID and npcID~=0 then\n            return {ID = npcID, name = name}\n        else\n            return nil\n        end\n    end\nend\n\naura_env.ZoneActiveQuests = function()\n    local list = {}\n    local zoneID = C_Map.GetBestMapForUnit(\"player\")\n    \n    for questID, questInfo in pairs(aura_env.saved.questCache) do\n        if questInfo and questInfo.active and questInfo.position and questInfo.position.zone then\n            if questInfo.position.zone == zoneID then\n                table.insert(list, questID)\n            end\n        end \n    end\n    \n    return #list > 0 and list or nil\nend\n\naura_env.RemoveWaypointByQuestID = function(questID)\n    if TomTom then\n        local wp = aura_env.saved.questCache[questID].waypoint\n        \n        if aura_env.config.verbose then print(aura_env.customGlobals.title .. \": Removing waypoint\") end\n        TomTom:RemoveWaypoint(wp)\n        if TomTom:IsValidWaypoint(wp) then\n            print(WrapTextInColorCode(\"Failed to remove\", \"ffff0000\"))\n        else\n            aura_env.saved.questCache[questID].waypoint = false\n        end\n    end\nend\n\naura_env.RemoveAllWaypoints = function()\n    if TomTom then\n        print(aura_env.customGlobals.title .. \": Removing all known waypoints\")\n        for _, info in pairs(aura_env.saved.questCache) do\n            if info and info.waypoint then\n                TomTom:RemoveWaypoint(info.waypoint)\n                info.waypoint = false\n            end\n        end\n    end\nend\n\naura_env.ClearRemovedWaypoints = function()\n    if TomTom then\n        if aura_env.config.debug then\n            print(aura_env.customGlobals.title .. \" clearing removed waypoints\")\n        end\n        for _, info in pairs(aura_env.saved.questCache) do\n            if info and info.waypoint then\n                if not(TomTom:IsValidWaypoint(info.waypoint)) then\n                    info.waypoint = false\n                end\n            end\n        end \n    end\nend\n\naura_env.ClearCache = function()\n    aura_env.saved.questCache = {}\n    print(aura_env.customGlobals.title .. \": Quest cache reset\")\nend",
