ctiveQuestID]\n                    local npc = questInfo.npc\n                    local position = questInfo.position\n                    \n                    -- if the world quest is a pet battle world quest, the npc id is stored in the cache, and the rematch setting is turned on, attempt to load a rematch team\n                    if aura_env.QuestIsPetBattle(activeQuestID) and Rematch and RematchSettings and aura_env.config.rematch and npc then\n                        if not(RematchSettings.loadedTeam == npc.ID) then\n                            if not aura_env.last or aura_env.last < GetTime() - 1 then\n                                aura_env.last = GetTime()\n                                \n                                Rematch:LoadTeam(npc.ID)\n                                if RematchSettings.loadedTeam == npc.ID then\n                                    if aura_env.config.verbose then \n                                        print(aura_env.customGlobals.title .. \": Rematch team for \" .. WrapTextInColorCode(npc.name, \"ffffff00\") .. \" successfully loaded\")\n                                    end\n                                    C_Timer.After(1, function()\n                                            for slot = 1, 3 do\n                                                local petID = C_PetJournal.GetPetLoadOutInfo(slot)\n                                                if petID then\n                                                    local hurt = C_PetJournal.PetIsHurt(petID)\n                                                    if hurt then\n                                                        print(aura_env.customGlobals.title ..  \": \" .. WrapTextInColorCode(\"One or more pets is not at full health\", \"ffff0000\"))\n                                                        break\n                                                    end\n                                                end\n                                            end\n                                    end)\n                                else\n                                    if not aura_env.last or aura_env.last < GetTime() - 20 then\n                                        aura_env.last = GetTime()\n                                        \n                                        print(aura_env.customGlobals.title .. \": \" .. WrapTextInColorCode(\"Rematch team failed to load\", \"ffff0000\"))\n                                    end\n                                end\n                            end\n                        end\n                    end\n                    \n                    -- set a tomtom waypoint if the position is stored in the cache\n                    if aura_env.config.tomtom and position and questInfo.waypoint == false then\n                        local questName = C_TaskQuest.GetQuestInfoByQuestID(activeQuestID)\n                        local options = {title = \"WQR: \" .. questName, persistent = false}\n                        aura_env.saved.questCache[activeQuestID].waypoint = TomTom:AddWaypoint(position.zone, position.x/100, position.y/100, options)\n                        placedWP = true\n                    end\n                end\n            end\n            \n            aura_env.ClearRemovedWaypoints()\n        end\n        \n        if placedWP then\n            TomTom:SetClosestWaypoint(false)\n        end\n        \n        if e == \"PLAYER_ENTERING_WORLD\" or e == \"QUEST_LOG_UPDATE\" or e == \"QUEST_TURNED_IN\" or e == \"TRIGGER\" then\n            aura_env.GetWorldQuests()\n        end\n        \n        if aura_env.config.debug and DevTool then\n            DevTool:AddData(aura_env.saved, \"WQR saved data\")\n        end\n        return true\n    end\nend\n\n\n",
