						["custom"] = "function(e,f)\n    if e == \"WA_MUROPAKETI_RTB_GET_BUILD_INFO\" and f == nil then return end\n    if ((e == \"CVAR_UPDATE\") and f ~= \"nameplateShowEnemies\") or ((e == \"WA_DELAYED_EQUIPMENT_UPDATE\") and f == nil) then return end\n    local a = aura_env\n    local c = a.config\n    a.KirReq = c.KirReg\n    \n    if e == \"PLAYER_EQUIPMENT_CHANGED\" then C_Timer.After(0.5, function() WeakAuras.ScanEvents(\"WA_DELAYED_EQUIPMENT_UPDATE\", true) end) return end --Delays aura firing when changing equipments\n    \n    if (e == \"ADDON_LOADED\" and f ~= nil) or e == \"LOADING_SCREEN_DISABLED\" then C_Timer.After(3.75, function() WeakAuras.ScanEvents(\"WA_ADDON_LOADED_DELAYED\", true) end) return end --used to initially load build during login\n    \n    --if e == \"PLAYER_ENTERING_WORLD\" and f == true then return end --Not doing anything on this event during initial login\n    \n    if ((e == \"CVAR_UPDATE\") and f == \"nameplateShowEnemies\") or e ~= \"CVAR_UPDATE\" then\n        \n        if GetCVar(\"nameplateShowEnemies\") == \"0\" and c.BladeFlurry == true and c.Warnings == true and WeakAuras.IsOptionsOpen() == false and a.NamePlateErr == false then\n            a.NamePlateErr = true\n            print(\"\\nWA - |cFF00FF00\"..a.id..\"|r - |cFFFFFF00Warning!|r\\nBlade flurry reminder requires enemy nameplates to be enabled.\")\n        end \n        \n        if e == \"CVAR_UPDATE\" then\n            return \n        end\n    end \n    \n    if a.LastTriggered + 0.2 > GetTime() then return end --Don't want this part of the aura to run too often. Just in case where multiple events are fired at once. This limits it to firing at max once every 0.2 sec.\n    \n    a.LastTriggered = GetTime()\n    \n    if e == \"TRAIT_CONFIG_UPDATED\" then \n        a.RTBModified = {false,false,false,false,false,false}\n        a.RtbVValModErr = {false,false,false,false,false,false}\n    end\n    \n    if not InCombatLockdown() or #a.BuffVal == 0 then\n        --[[tinsert(a.BuffVal,1,c.RuthlessPrecision) --193357\n        tinsert(a.BuffVal,2,c.SkullAndCrossbones) --199603\n        tinsert(a.BuffVal,3,c.Broadside) --193356\n        tinsert(a.BuffVal,4,c.TrueBearing) --193359\n        tinsert(a.BuffVal,5,c.GrandMelee) --193358\n        tinsert(a.BuffVal,6,c.BuriedTreasure) --199600]]--\n        \n        for BaseVals = 1, #a.RollBase - 1 do \n            tinsert(a.BuffVal,BaseVals, a.RollBase[BaseVals]) --199600\n        end\n        \n        a.AoEBonus = a.RollBase[#a.RollBase]--c.AoEBonus;\n        \n        for i, v  in ipairs(c.modifier) do\n            local Talented = true\n            for j, k in ipairs(v.talent) do \n                if GetSpellInfo(k.id) == nil then \n                    print(\"|cFF00FF00\"..a.id..\"|r - |cFFFF0000Configuration error!|r\\nEntry id \"..i..\" talent \"..j..\" spell id of \"..k.id..\" is not valid.\")\n                    Talented = false\n                    break;\n                end\n                \n                if (not IsPlayerSpell(k.id) and k.inverse == false) or (IsPlayerSpell(k.id) and k.inverse == true) then\n                    if a.Debug == true then print(\"Entry failure \"..i..\" - Talent Condition not met on talent entry \"..j .. \" - talent id \"..k.id) end\n                    Talented = false\n                    break;\n                end\n            end\n            \n            local ValidTier = (select(1,WeakAuras.GetNumSetItemsEquipped(a.TierID[v.Tier])) >= (v.Pieces * 2) and select(1,WeakAuras.GetNumSetItemsEquipped(a.TierID[v.Tier])) < (v.Pieces * 2) + 2)\n            \n            if Talented == true and v.Enabled and (v.RequireTier == false or ValidTier == true) then\n                for RTBBuffs = 1, #a.TalentBuffs do \n                    --  if v[\"RTBbuffs\"][\"RTB\"..a.TalentBuffs[RTBBuffs]] == true then\n                    a.BuffVal[RTBBuffs] = v[\"RTBbuffs\"][\"Mod\"..a.TalentBuffs[RTBBuffs]]\n                    if a.BuffVal[RTBBuffs] < 1 then a.BuffVal[RTBBuffs] = 1 end\n                    a.AoEBonus = v[\"RTBbuffs\"][\"GrandMeleeAoE\"]\n                    --  end\n                end\n                \n                if v.KirVal ~= 0 and IsPlayerSpell(381989) then\n                    a.KirReq = v.KirVal \n                end\n                \n                local SetName = i..\" - Unknown Name\"\n                if v.SetName ~= \"\" then \n                    SetName = v.SetName\n                end\n                if WeakAuras.IsOptionsOpen() == false  then\n                    --WA_RTB_CUR_ACTIVE_RULESET Name of active RTB modifier, Roll weights, Roll id:s, same build as last time when event was fired\n                    WeakAuras.ScanEvents(\"WA_RTB_CUR_ACTIVE_RULESET\", SetName, a.BuffVal, a.Rolls, a.LastBuild == SetName)\n                    if a.LastBuild ~= SetName then\n                        a.LastBuild = SetName\n                        if c.BuildPrint == true then\n                            print(\"|cFF00FF00\"..a.id..\"|r - RTB modifier \"..i..\" |cFF71D5FF[\"..SetName..\"]|r loaded.\") \n                        end\n                    end\n                    \n                end\n                \n                break; \n            end\n            if i == #c.modifier then \n                if c.Events == true then\n                    WeakAuras.ScanEvents(\"WA_RTB_CUR_ACTIVE_RULESET\", \"Base Values\", a.RollBase, a.Rolls, a.LastBuild == \"Base Values\")\n                end\n                a.LastBuild = \"Base Values\"\n            end\n        end\n        \n        \n        \n        if c.RollTest == true and WeakAuras.IsOptionsOpen() then\n            local TestVal = 0\n            local Matches = 0\n            local Icons = \"\"\n            local FakeRolls = {}\n            local AoE = \"\"\n            \n            local TestBuffs = {c.TestRuthlessPrecision, c.TestSkullAndCrossbones, c.TestBroadside, c.TestTrueBearing, c.TestGrandMelee, c.TestBuriedTreasure}\n            \n            for i = 1, #TestBuffs do \n                if TestBuffs[i] == true then \n                    tinsert(FakeRolls, a.Rolls[i])\n                    \n                    if c.AoEScen == true and a.Rolls[i] == 193358 then\n                        TestVal = TestVal + a.AoEBonus\n                        AoE = \" (AOE)\"\n                    end\n                    \n                    TestVal = TestVal + a.BuffVal[i] Icons = Icons..\"|T\"..select(3,GetSpellInfo(a.Rolls[i]))..\":0|t \" Matches = Matches + 1 \n                end\n            end\n            \n            if TestVal >= a.Reqs[5] and a.LoadedDice(FakeRolls, not c.LoadDice, c.AoEScen) then TestVal = a.Reqs[6] end \n            if Matches == 1 or Matches == 2 or Matches == 5 or (select(1,WeakAuras.GetNumSetItemsEquipped(1566)) >= 4 and Matches == 3) or (select(1,WeakAuras.GetNumSetItemsEquipped(1566)) >= 4 and Matches == 6) then\n                for loopz = 1, #a.Reqs do\n                    if TestVal >= a.Reqs[loopz] then \n                        Icons = Icons..\" - |\"..a.Ranks[loopz]..\"|r\"\n                        if loopz == 1 and c.Audio == true then\n                            a.SoundHandler(c[\"RollAudio\"][\"Chant\"], c.Audio, a.AudioChannels[c.Channel], false)\n                        end\n                        if c.Audio == true then\n                            a.SoundHandler(a.Audio[loopz], c.Audio, a.AudioChannels[c.Channel], false)\n                        end\n                        break;\n                    end\n                    --z = z + 1\n                end\n                print(\"|cFFFFFF00RTB Roll Test|r  \"..Icons..\" (\"..TestVal..\")\"..AoE)\n            end\n        end\n    end\nend",
