						["names"] = {
						},
						["check"] = "event",
						["custom"] = "function (allstates, event, ...)\n    if event == \"OPTIONS\" then\n        local previewSpells = {\n            [1] = 31821,\n            [2] = 97462,\n            [3] = 145629\n        }\n        \n        for i = 1, 3 do\n            local spellID = previewSpells[i]\n            local name, _, icon = GetSpellInfo(spellID)\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = aura_env.config.defaultDuration,\n                expirationTime = GetTime() + aura_env.config.defaultDuration,\n                name = aura_env.config.includeSpellName and name,\n                icon = icon\n            }\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, _, _, _, _, spellID = ...\n        \n        if aura_env.queued.event[subEvent][spellID] then\n            aura_env.queued.event[subEvent][spellID].count = (aura_env.queued.event[subEvent][spellID].count or 0) + 1\n            local count = aura_env.queued.event[subEvent][spellID].count\n            local id = aura_env.id\n            local encounterStarted = aura_env.encounterStarted\n            \n            if aura_env.queued.event[subEvent][spellID][count] then\n                for _, reminder in ipairs(aura_env.queued.event[subEvent][spellID][count]) do\n                    local start = reminder.start\n                    local duration = reminder.duration\n                    local text = reminder.text\n                    \n                    if start > 0 then\n                        table.insert(\n                            aura_env.timers,\n                            C_Timer.NewTimer(start,\n                                function()\n                                    WeakAuras.ScanEvents(\"LIQUID_ERT_REMINDER\", id, encounterStarted, text, start, duration)\n                                end\n                            )\n                        )\n                    else -- If the timer should start showing immediately on phase change, just fire the event directly\n                        WeakAuras.ScanEvents(\"LIQUID_ERT_REMINDER\", id, encounterStarted, text, start, duration)\n                    end\n                end\n            end\n        end\n    elseif event == \"BigWigs_SetStage_Liquid\" then\n        local id, phase = ...\n        \n        -- Make sure the event was sent from this aura\n        if id ~= aura_env.id then return end\n        \n        -- If we are already in this phase, ignore the phase event\n        -- We always \"fake\" a BigWigs_SetStage on pull to cover for BigWigs not sending one on fights that have no phase changes\n        -- Because of this, on fights that do have phase changes we will get a double phase event on pull\n        if phase == aura_env.phase then return end\n        \n        -- Increment the number of times we have seen this phase\n        aura_env.phaseCount[phase] = (aura_env.phaseCount[phase] or 0) + 1\n        \n        aura_env.phase = phase\n        local phaseCount = aura_env.phaseCount[phase]\n        local id = aura_env.id\n        local encounterStarted = aura_env.encounterStarted\n        \n        if aura_env.queued.phase[phase] and aura_env.queued.phase[phase][phaseCount] then\n            for _, reminder in ipairs(aura_env.queued.phase[phase][phaseCount]) do\n                local start = reminder.start\n                local duration = reminder.duration\n                local text = reminder.text\n                \n                if start > 0 then\n                    table.insert(\n                        aura_env.timers,\n                        C_Timer.NewTimer(start,\n                            function()\n                                WeakAuras.ScanEvents(\"LIQUID_ERT_REMINDER\", id, encounterStarted, text, start, duration, phase)\n                            end\n                        )\n                    )\n                else -- If the timer should start showing immediately on phase change, just fire the event directly\n                    WeakAuras.ScanEvents(\"LIQUID_ERT_REMINDER\", id, encounterStarted, text, start, duration, phase)\n                end\n            end\n        end\n    elseif event == \"BigWigs_StartBar_Liquid\" then\n        local id, barID = ...\n        \n        -- Make sure the event was sent from this aura\n        if id ~= aura_env.id then return end\n        \n        local bar = WeakAuras.GetBigWigsTimerById(barID)\n        \n        if bar and not bar.cast then -- Don't create reminders for BigWigs cast timers\n            local phases = {aura_env.phase, \"ALL\"} -- Valid phases in aura_env.queued\n            local phaseCounts = {aura_env.phaseCount[aura_env.phase] or 1, \"ALL\"} -- Valid phase counts in aura_env.queued\n            local spellID = bar.spellId and tonumber(bar.spellId)\n            local spellCounts = {bar.count and tonumber(bar.count) or 1, \"ALL\"} -- Valid spell counts in aura_env.queued\n            local expirationTime = bar.expirationTime and tonumber(bar.expirationTime)\n            local encounterStarted = aura_env.encounterStarted\n            \n            -- The triply nested for loop here is necessary because phase, phaseCount, and spellCount all have an option for \"ALL\"\n            -- This means that there are 8 different subtables in aura_env.queued that could hold relevant reminders\n            -- Namely, every combination of the current phase/phaseCount/spellCount, or a wildcard for each\n            for _, phase in ipairs(phases) do\n                for _, phaseCount in ipairs(phaseCounts) do\n                    for _, spellCount in ipairs(spellCounts) do\n                        if aura_env.queued.bigwigs[phase] and aura_env.queued.bigwigs[phase][phaseCount] and aura_env.queued.bigwigs[phase][phaseCount][spellID] and aura_env.queued.bigwigs[phase][phaseCount][spellID][spellCount] then\n                            for _, reminder in ipairs(aura_env.queued.bigwigs[phase][phaseCount][spellID][spellCount]) do\n                                local duration = reminder.duration\n                                local offset = reminder.offset\n                                local start = expirationTime - GetTime() - duration + offset\n                                local text = reminder.text\n                                \n                                if start > 0 then\n                                    table.insert(\n                                        aura_env.timers,\n                                        C_Timer.NewTimer(start,\n                                            function()\n                                                WeakAuras.ScanEvents(\"LIQUID_ERT_REMINDER\", id, encounterStarted, text, start, duration, phase, barID, offset)\n                                            end\n                                        )\n                                    )\n                                else -- If the timer should start showing immediately on phase change, just fire the event directly\n                                    WeakAuras.ScanEvents(\"LIQUID_ERT_REMINDER\", id, encounterStarted, text, start, duration, phase, barID, offset)\n                                end\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    elseif event == \"BigWigs_StopBar_Liquid\" then\n        local id, barID = ...\n        \n        -- Make sure the event was sent from this aura\n        if id ~= aura_env.id then return end\n        \n        for _, state in pairs(allstates) do\n            if state.barID and state.barID == barID then\n                local offset = state.offset\n                \n                -- If the bar has a positive offset, set the remaining duration equal to the offset\n                -- This is necessary because frequently a BigWigs bar is stopped early when a cast starts, even if it's very shortly before the expiration time\n                -- We don't want the bar to disappear immediately when this happens if it has a positive offset\n                if offset and offset > 0 then\n                    state.expirationTime = GetTime() + offset\n                else\n                    state.show = false\n                end\n                \n                state.changed = true\n            end\n        end\n        \n        return true\n    elseif event == \"BigWigs_PauseBar_Liquid\" then\n        local id, barID = ...\n        \n        -- Make sure the event was sent from this aura\n        if id ~= aura_env.id then return end\n        \n        for _, state in pairs(allstates) do\n            if state.barID and state.barID == barID then\n                state.remaining = state.expirationTime - GetTime()\n                state.changed = true\n                state.paused = true\n            end\n        end\n        \n        return true\n    elseif event == \"BigWigs_ResumeBar_Liquid\" then\n        local id, barID = ...\n        \n        -- Make sure the event was sent from this aura\n        if id ~= aura_env.id then return end\n        \n        for _, state in pairs(allstates) do\n            if state.barID and state.barID == barID then\n                state.paused = false\n                state.expirationTime = GetTime() + (state.remaining or 0)\n                state.changed = true\n            end\n        end\n        \n        return true\n    elseif event == \"LIQUID_ERT_REMINDER\" then\n        local id, encounterStarted, spellID, start, duration, phase, barID, offset = ...\n        \n        -- Make sure the event was sent from this aura\n        if id ~= aura_env.id then return end\n        \n        if barID then\n            local bar = WeakAuras.GetBigWigsTimerById(barID)\n            if not bar then return end\n        end\n        \n        if encounterStarted ~= aura_env.encounterStarted then return end -- Ignore events sent during previous fights\n        if phase and phase ~= \"ALL\" and phase ~= aura_env.phase then return end -- Ignore events for phases we are not in\n        \n        -- Account for events that happen earlier than the regular duration of a reminder\n        duration = start < 0 and duration + start or duration\n        \n        local name, _, icon = GetSpellInfo(spellID)\n        \n        table.insert(\n            allstates,\n            {\n                show = true,\n                changed = true,\n                progressType = \"timed\",\n                duration = duration,\n                expirationTime = GetTime() + duration,\n                icon = icon,\n                name = aura_env.config.includeSpellName and name,\n                barID = barID,\n                offset = offset, -- This needs to be saved in case BigWigs_StopBar is fired\n                autoHide = true\n            }\n        )\n        \n        -- Play TTS (if enabled)\n        if aura_env.ttsVoiceID > -1 then\n            C_VoiceChat.SpeakText(\n                aura_env.ttsVoiceID,\n                name,\n                Enum.VoiceTtsDestination.RemoteTransmission,\n                C_TTSSettings and C_TTSSettings.GetSpeechRate() or 0,\n                C_TTSSettings and C_TTSSettings.GetSpeechVolume() or 100\n            )\n        end\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        -- Stop any timers that are still running from when the aura was previously loaded\n        if aura_env.timers then\n            for _, timer in pairs(aura_env.timers) do\n                timer:Cancel()\n            end\n        end\n        \n        aura_env.phase = nil\n        aura_env.timers = {} -- Active timers\n        aura_env.encounterStarted = GetTime() -- Used to verify that incoming timers were fired during the current pull\n        aura_env.phaseCount = {} -- Number of times a phase has been seen. Used for encounters where phases repeat.\n        aura_env.gsubAutoColorData = {} -- Unit name (and nickname) to colored unit name\n        \n        -- Queued timers, fired on phase change or combat log event\n        aura_env.queued = {\n            phase = {},\n            bigwigs = {},\n            event = {\n                SPELL_CAST_START = {},\n                SPELL_CAST_SUCCESS = {},\n                SPELL_AURA_APPLIED = {},\n                SPELL_AURA_REMOVED = {}\n            }\n        }\n        \n        -- If MRT is not enabled, don't do anything.\n        if not (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\")) then return end\n        \n        -- Colors player names (and nicknames) according to their class\n        for unit in WA_IterateGroupMembers() do\n            local name = UnitName(unit)\n            \n            aura_env.gsubAutoColorData[name] = WA_ClassColorName(unit)\n            \n            -- Color nicknames according to the class they are currently playing\n            if LiquidAPI then\n                local nickname = LiquidAPI:GetName(name)\n                \n                if nickname and nickname ~= name then\n                    local class = UnitClassBase(unit)\n                    aura_env.gsubAutoColorData[nickname] = string.format(\"|c%s%s|r\", RAID_CLASS_COLORS[class].colorStr, nickname)\n                end\n            end\n        end\n        \n        local notes = {\n            aura_env.config.enableSharedNote and _G.VExRT.Note.Text1 or nil,\n            aura_env.config.enablePersonalNote and _G.VExRT.Note.SelfText or nil\n        }\n        \n        local playerName = UnitName(\"player\")\n        local playerNickname = LiquidAPI and LiquidAPI:GetName(\"player\") or playerName\n        local playerSpecID, _, _, _, playerRole = GetSpecializationInfo(GetSpecialization())\n        local playerSpecType = aura_env.specToType[playerSpecID]\n        local playerGroup = UnitInRaid(\"player\") and GetRaidRosterInfo(UnitInRaid(\"player\")) or 1\n        \n        for _, note in pairs(notes) do\n            for line in note:gmatch(\"[^\\r\\n]+\") do\n                local myReminders = {}\n                local options, reminders = line:match(\"^{(.-)}%d+:%d+%s%-%s.-%s%-%s(.+)\")\n                \n                -- If the note is not showing a static timer, the above pattern match will have failed\n                -- Pattern match again, assuming there is no static timer shown\n                if not reminders then\n                    options, reminders = line:match(\"^{(.-)}.-%s%-%s(.+)\")\n                end\n                \n                -- First check which reminders on this line are relevant to the player\n                -- If they are relevant, save them in the myReminders table so we can queue them later on\n                if reminders then\n                    for reminder in reminders:gmatch(\"(.-)%s%s\") do\n                        local target, text = reminder:match(\"(.-)%s({.+})\")\n                        \n                        if target and text then\n                            target = target:match(\"||c%x%x%x%x%x%x%x%x(.-)||r\") or target -- Remove name coloring (if any)\n                            text = text:match(\"{[Tt][Ee][Xx][Tt]}(.-){/[Tt][Ee][Xx][Tt]}\") or text -- Remove {text} tags (if any)\n                            local role = target:upper():match(\"{ROLE:(.-)}\")\n                            local group = target:lower():match(\"group:(%d)+\")\n                            \n                            if target:lower() == \"{everyone}\" then -- Reminders for everyone\n                                table.insert(myReminders, text)\n                            elseif role and role == playerRole then -- Reminders for a specific role (tank, healer, damager)\n                                table.insert(myReminders, text)\n                            elseif group and tonumber(group) == playerGroup then -- Reminders for a specific raid subgroup\n                                table.insert(myReminders, text)\n                            elseif target == playerSpecType then -- Reminders for a specific spec type (melee, ranged)\n                                table.insert(myReminders, text)\n                            elseif target == playerName or target == playerNickname then -- Reminders for a specific player (matches both character name and nickname)\n                                table.insert(myReminders, text)\n                            end\n                        end\n                    end\n                end\n                \n                -- Only continue if there were relevant reminders on this line\n                if #myReminders > 0 then\n                    -- Fill this table with the options configuration for this line\n                    -- These determine when the reminder shows up\n                    -- Some options can have more than one argument, this is why each entry is an array.\n                    -- The table is filled with some default values in case the note does not provide them\n                    local optionsTable = {\n                        time = {0, 0},\n                        offset = {0},\n                        duration = {aura_env.config.defaultDuration}\n                    }\n                    \n                    for option in options:gmatch(\"[^,]+\") do\n                        local optionArgs = strsplittable(\":\", option)\n                        \n                        if #optionArgs >= 2 then\n                            optionsTable[optionArgs[1]] = {}\n                            \n                            -- The \"time\" option is handled a bit differently\n                            -- If it has a single argument it's interpreted as seconds\n                            -- If it has multiple arguments, the first is interpreted as minutes\n                            if optionArgs[1] == \"time\" and #optionArgs == 2 then\n                                optionsTable.time[2] = optionArgs[2] -- Handle the special case where there is only one argument\n                            else\n                                for i = 2, #optionArgs do\n                                    optionsTable[optionArgs[1]][i - 1] = tonumber(optionArgs[i]) or optionArgs[i]\n                                end\n                            end\n                        end\n                    end\n                    \n                    for _, text in ipairs(myReminders) do\n                        local spellReminder = text:match(\"^{spell:(%d+)}$\")\n                        \n                        -- If the reminder is for a single spell, use the spell ID to find the TTS file\n                        if spellReminder then\n                            -- Format the reminder before queueing\n                            text = tonumber(spellReminder)\n                            \n                            -- Detect the type of reminder from the entries in optionsTable\n                            -- This will decide where in the queued reminders table they are placed\n                            local reminderType = \"TIME\"\n                            \n                            if optionsTable.BW then\n                                reminderType = \"BW\"\n                            else\n                                for eventType in pairs(aura_env.eventTypes) do\n                                    if optionsTable[eventType] then\n                                        reminderType = \"CLEU\"\n                                        break\n                                    end\n                                end\n                            end\n                            \n                            -- Before we queue the reminders, first create some more easy to use aliases for the optionsTable entries\n                            -- We also make sure the optionsTable entries are correctly formatted here (numbers where they are expected to be numbers)\n                            local minutes = tonumber(optionsTable.time[1]) or 0\n                            local seconds = tonumber(optionsTable.time[2]) or 0\n                            local duration = tonumber(optionsTable.duration[1]) or aura_env.config.defaultDuration\n                            local start = minutes * 60 + seconds - duration\n                            local phase = optionsTable.phase and optionsTable.phase[1] and tonumber(optionsTable.phase[1])\n                            local phaseCount = optionsTable.phase and optionsTable.phase[1] and tonumber(optionsTable.phase[2])\n                            \n                            -- Queue the reminders\n                            if reminderType == \"TIME\" then\n                                -- If no phase and phase count were specified, assume the timers are relative to the pull\n                                -- i.e. the first time phase 1 starts\n                                phase = phase or 1\n                                phaseCount = phaseCount or 1\n                                \n                                if not aura_env.queued.phase[phase] then aura_env.queued.phase[phase] = {} end\n                                if not aura_env.queued.phase[phase][phaseCount] then aura_env.queued.phase[phase][phaseCount] = {} end\n                                \n                                table.insert(\n                                    aura_env.queued.phase[phase][phaseCount],\n                                    {\n                                        start = start,\n                                        text = text,\n                                        duration = duration,\n                                    }\n                                )\n                            elseif reminderType == \"CLEU\" then\n                                local eventType\n                                local spellID\n                                local spellCount\n                                \n                                for eventShorthand, eventFull in pairs(aura_env.eventTypes) do\n                                    if optionsTable[eventShorthand] then\n                                        eventType = eventFull\n                                        spellID = optionsTable[eventShorthand][1] and tonumber(optionsTable[eventShorthand][1])\n                                        spellCount = optionsTable[eventShorthand][2] and tonumber(optionsTable[eventShorthand][2]) or 1\n                                    end\n                                end\n                                \n                                if eventType and spellID then\n                                    if not aura_env.queued.event[eventType][spellID] then aura_env.queued.event[eventType][spellID] = {} end\n                                    if not aura_env.queued.event[eventType][spellID][spellCount] then aura_env.queued.event[eventType][spellID][spellCount] = {} end\n                                    \n                                    table.insert(\n                                        aura_env.queued.event[eventType][spellID][spellCount],\n                                        {\n                                            start = start,\n                                            text = text,\n                                            duration = duration,\n                                        }\n                                    )\n                                end\n                            elseif reminderType == \"BW\" then\n                                -- If no phase and phase count were specified, assume we want to match the timer regardless of phase\n                                phase = phase or \"ALL\"\n                                phaseCount = phaseCount or \"ALL\"\n                 