			["preferToUpdate"] = false,
			["customText"] = "",
			["yOffset"] = -84.00018310546875,
			["anchorPoint"] = "CENTER",
			["cooldownSwipe"] = true,
			["cooldownEdge"] = false,
			["actions"] = {
				["start"] = {
					["custom"] = "",
					["do_custom"] = false,
