             local spellID\n                                local spellCount\n                                \n                                for eventShorthand, eventFull in pairs(aura_env.eventTypes) do\n                                    if optionsTable[eventShorthand] then\n                                        eventType = eventFull\n                                        spellID = optionsTable[eventShorthand][1] and tonumber(optionsTable[eventShorthand][1])\n                                        spellCount = optionsTable[eventShorthand][2] and tonumber(optionsTable[eventShorthand][2]) or 1\n                                    end\n                                end\n                                \n                                if eventType and spellID then\n                                    if not aura_env.queued.event[eventType][spellID] then aura_env.queued.event[eventType][spellID] = {} end\n                                    if not aura_env.queued.event[eventType][spellID][spellCount] then aura_env.queued.event[eventType][spellID][spellCount] = {} end\n                                    \n                                    table.insert(\n                                        aura_env.queued.event[eventType][spellID][spellCount],\n                                        {\n                                            start = start,\n                                            text = text,\n                                            duration = duration,\n                                        }\n                                    )\n                                end\n                            elseif reminderType == \"BW\" then\n                                -- If no phase and phase count were specified, assume we want to match the timer regardless of phase\n                                phase = phase or \"ALL\"\n                                phaseCount = phaseCount or \"ALL\"\n                                \n                                local spellID = optionsTable.BW and optionsTable.BW[1] and tonumber(optionsTable.BW[1])\n                                local spellCount = optionsTable.BW and optionsTable.BW[2] and tonumber(optionsTable.BW[2]) or \"ALL\"\n                                local offset = tonumber(optionsTable.offset[1]) or 0\n                                \n                                if spellID then\n                                    if not aura_env.queued.bigwigs[phase] then aura_env.queued.bigwigs[phase] = {} end\n                                    if not aura_env.queued.bigwigs[phase][phaseCount] then aura_env.queued.bigwigs[phase][phaseCount] = {} end\n                                    if not aura_env.queued.bigwigs[phase][phaseCount][spellID] then aura_env.queued.bigwigs[phase][phaseCount][spellID] = {} end\n                                    if not aura_env.queued.bigwigs[phase][phaseCount][spellID][spellCount] then aura_env.queued.bigwigs[phase][phaseCount][spellID][spellCount] = {} end\n                                    \n                                    table.insert(\n                                        aura_env.queued.bigwigs[phase][phaseCount][spellID][spellCount],\n                                        {\n                                            text = text,\n                                            duration = duration,\n                                            offset = offset,\n                                        }\n                                    )\n                                end\n                            end\n                        end\n                    end\n                end\n            end\n        end\n        \n        -- BigWigs only sends phase events on fights that have multiple phases\n        -- Because we rely on this event to initiate the reminders, we \"fake\" it on pull\n        -- Multiple of the same phase events are ignored, so this should not cause any issues\n        WeakAuras.Scan